    private void hitungBalokActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungBalokActionPerformed
        // TODO add your handling code here:
        double panjang = Double.parseDouble(panjangBalok.getText());
        double lebar = Double.parseDouble(lebarBalok.getText());
        double tinggi = Double.parseDouble(tinggiBalok.getText());
        
        double luasPermukaan = (2 * ((panjang * lebar) + (panjang * tinggi) + (lebar  * tinggi)));
        luasPermukaanBalok.setText(String.valueOf(luasPermukaan));
        
        double Volume = (panjang * lebar * tinggi);
        VolumeBalok.setText(String.valueOf(Volume));
        
    }//GEN-LAST:event_hitungBalokActionPerformed

