    private void hitungBolaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungBolaActionPerformed
        // TODO add your handling code here:
        double jejari = Double.parseDouble(jejariBola.getText());
        
        double luasPermukaan = (2 * (4/3 * Math.PI * (jejari * jejari * jejari)));
        luasPermukaanBola.setText(String.valueOf(luasPermukaan));
        
        double Volume = (4 * Math.PI * (jejari * jejari));
        volumeBola.setText(String.valueOf(Volume));
    }//GEN-LAST:event_hitungBolaActionPerformed

    private void hitungKubusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungKubusActionPerformed
        // TODO add your handling code here:
        double sisi = Double.parseDouble(sisiKubus.getText());
        
        double luasPermukaan = (sisi * sisi);
        luasPermukaanKubus.setText(String.valueOf(luasPermukaan));
        
        double Volume = (4 * sisi);
        VolumeKubus.setText(String.valueOf(Volume));
    }//GEN-LAST:event_hitungKubusActionPerformed

    private void hitungPrismaSegitigaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungPrismaSegitigaActionPerformed
        // TODO add your handling code here:
        double alas = Double.parseDouble(alasSegitiga.getText());
        double tinggip = Double.parseDouble(tinggipris.getText());
        double tinggi = Double.parseDouble(tinggiSegitiga.getText());
        
        double sisiMiringSegitiga  = (Math.sqrt(alas * alas) + (tinggi * tinggi));
        
        double luasPermukaan = ((2 * (0.5 * alas * tinggi)) + ((alas + tinggi + sisiMiringSegitiga) * tinggip));
        luasPermukaanPrismaSegitiga.setText(String.valueOf(luasPermukaan));
        
        double Volume = ((0.5 * alas * tinggi) * tinggip);
        VolumePrismaSegitiga.setText(String.valueOf(Volume));
    }//GEN-LAST:event_hitungPrismaSegitigaActionPerformed

